import { useState } from "react";
import { useQuery } from "react-query";
import countAVE from "../components/countAVE";
import { getTable } from "../util/api";
import countALL from "../util/countALL";

const useTotalPros = (urlReqCompanyName: string, prosType: string) => {
  const [totalPros, setTotalPros] = useState(0);
  const [totalAVE, setTotalAVE] = useState(0);
  const { data } = useQuery<any>(["table"], () => getTable(urlReqCompanyName), {
    onSuccess: ({ data }) => {
      setTotalPros(countALL(data, prosType));
      setTotalAVE(countAVE(data));
    },
  });
  return totalPros;
};

export default useTotalPros;
