import axios from "axios";
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

// 1. MainPage에서 넘어온 id를 기준으로, 서버에 데이터 요청을 보낸다.
//

const CompanyPage = () => {
  const location = useLocation();
  const urlRequest = location.state["companyName"];
  const [dataList, setDataList] = useState("");

  useEffect(() => {
    getList();
  }, []);

  const getList = () => {
    axios
      .get(
        // "https://sheet.best/api/sheets/325137bc-19de-426f-a61c-bae2208f94db/회사명/카카오"
        "https://sheet.best/api/sheets/325137bc-19de-426f-a61c-bae2208f94db/companyName/kakao"
      )
      .then((response: any) => {
        // 결과값 [{'회사명':카카오, '장점1':'카카오장점1', '장점2':'카카오장점2'}, {'회사명':카카오, '장점1':'카카오장점1', '장점2':'카카오장점2'}]
        console.log(response.data);
        const wholeData = response.data;
        const 장점1: number[] = [];
        {
          wholeData.map((item: any) => {
            console.log(item.장점1);
            장점1.push(Number(item.장점1));
          });
        }
        // 장점1 :
        console.log(장점1);
        let result: number = 장점1.reduce(
          (total: number, value: number) => (total += value),
          0
        );
        console.log("result:", result);
      });
  };
  return (
    <div>
      <h3>urlRequest:{urlRequest}</h3>
      {/* <h3>dataList:{dataList}</h3> */}
    </div>
  );
};

export default CompanyPage;
