import { useEffect, useState } from "react";
import useCountALL from "../components/useCountALL";
import useCountAVE from "../components/useCountAVE";
import { supabase } from "../util/supabase";

const useGetData = (urlReqCompanyName: string) => {
  const [companyData, setCompanyData] = useState([{}]);
  const [totalPros1, setTotalPros1] = useState(0);
  const [commuteAve, setCommuteAve] = useState(0);

  // supabase 잘 연결되었는지 확인
  console.log("supabase:", supabase);
  // 총점1개, 장점1개 state 만들어보기

  // supabase에서 table 가져오는 함수
  const useGetTable = async () => {
    const { data, error } = await supabase
      .from("SurveyData")
      .select()
      .eq("company", `${urlReqCompanyName}`);

    if (data) {
      console.log(data);
      setCompanyData(data);
      console.log(companyData);
      const { totalPros1 } = useCountALL(companyData);
      const { commuteAve } = useCountAVE(companyData);

      console.log(totalPros1);
      console.log(commuteAve);
      setTotalPros1(totalPros1);
      setCommuteAve(commuteAve);
    }
    if (error) {
      console.log("에러가 나쪙 ㅠ.ㅠ", error);
    }
    // useEffect 부분
    useEffect(() => {
      useGetTable();
    }, []);
  };
  return { totalPros1, commuteAve };
};

export default useGetData;
